var exercise = require('workshopper-exercise')(),
  filecheck = require('workshopper-exercise/filecheck'),
  execute = require('workshopper-exercise/execute'),
  comparestdout = require('workshopper-exercise/comparestdout');

exercise = filecheck(exercise); // checks that the submission file actually exists
exercise = execute(exercise); // execute the solution and submission in parallel with spawn()
exercise = comparestdout(exercise); // compare stdout of solution and submission
module.exports = exercise;
