#!/usr/bin/env node

var workshopper = require('workshopper'),
  path = require('path');

function fpath (f) {
    return path.join(__dirname, f);
}

workshopper({
  name: 'introturf',
  title: 'Intro to Turf',
  subtitle: 'Learn how to use Turf to do spatial analysis',
  appDir: __dirname,
  menuItems: [],
  exerciseDir: fpath('./exercises/')
});
